package com.demo.rajasekhar.io.stream;

import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Base64;

public class Decodingimage {
	public static void main(String[] args) {
		String filePath = "rajresources/images/Abhibus.jpg";
		//String image = "\r\n";
	String image = "";
		FileDecode(filePath,image);
		
		
		
	}

	private static void FileDecode(String filePath, String image) {
		// TODO Auto-generated method stub
	    
		byte [] imageraj=Base64.getDecoder().decode(image);
		
		try (FileOutputStream fis = new FileOutputStream(filePath)) {
			
			fis.write(imageraj);
			System.out.println("Image update sucessfully");
			
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		
	}

}
